(243, 2, 240, '底层函数', '<h1>设计原则为 方便被调用</h1>\r\n\r\n<ol>\r\n	<li>Ge</li>\r\n	<li>G</li>\r\n	<li>D</li>\r\n	<li>C</li>\r\n	<li>M</li>\r\n	<li>R</li>\r\n	<li>T</li>\r\n	<li>W</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>truepath</li>\r\n	<li>includeIfExist</li>\r\n	<li>GetIP</li>\r\n	<li>URLES</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>halt</li>\r\n	<li>error404</li>\r\n	<li>errormsg</li>\r\n	<li>error500</li>\r\n</ol>\r\n', '##Ge\r\n\r\n\r\n代码 : \r\n\r\n    /**\r\n    * 返回G中的数据\r\n    */\r\n    function Ge($str = ''''){\r\n        return \\G\\Geter::getInstance()->get($str);\r\n    }\r\n\r\n执行\r\n\r\n    $ms = Ge(''user.info.32'') //执行 G/user.info 带参数32\r\n\r\n\r\n\r\n------------------------------------------------------------------------\r\n\r\n\r\n##G\r\n\r\n代码 :\r\n\r\n\r\n	//如果文件存在,则包含,否则返回空值\r\n    function G($filename){\r\n        if(file_exists($filename)){\r\n            return include $filename;\r\n        }\r\n        return [];\r\n    }\r\n    \r\n执行\r\n\r\n    $file = "./v.php";\r\n    $var = G($file);\r\n\r\n\r\n------------------------------------------------------------------------\r\n\r\n##D\r\n\r\n代码 : \r\n\r\n    /**\r\n     * @param $arr\r\n     * 取代print_r()的条数函数\r\n     */\r\n    function D($arr = []){\r\n        echo ''<pre>'';\r\n        print_r($arr);\r\n        echo ''</pre>'';\r\n    }\r\n\r\n##C\r\n\r\n代码 : \r\n\r\n    /**\r\n     * 获取和设置配置参数 支持批量定义\r\n     * 如果$key是关联型数组，则会按K-V的形式写入配置\r\n     * 如果$key是数字索引数组，则返回对应的配置数组\r\n     * @param string|array $key 配置变量\r\n     * @param array|null $value 配置值\r\n     * @return array|null\r\n     */\r\n    function C($key = '''',$value=null){\r\n        static $_config = array();\r\n        $args = func_num_args();\r\n        //返回所有值\r\n        if($args == 0){\r\n                return $_config;\r\n        }\r\n        //某一个返回值 这个值是字符串或者数组\r\n        if($args == 1){\r\n            if(is_string($key)){  //如果传入的key是字符串\r\n                return isset($_config[$key])?$_config[$key]:null;\r\n            }\r\n            if(is_array($key)){\r\n                if(array_keys($key) !== range(0, count($key) - 1)){  //如果传入的key是关联数组\r\n                    $_config = array_merge($_config, $key);\r\n                }else{\r\n                    $ret = array();\r\n                    foreach ($key as $k) {\r\n                        $ret[$k] = isset($_config[$k])?$_config[$k]:null;\r\n                    }\r\n                    return $ret;\r\n                }\r\n            }\r\n        }else{\r\n            //设置一个值\r\n            if(is_string($key)){\r\n                $_config[$key] = $value;\r\n            }else{\r\n                halt(''传入参数不正确'');\r\n            }\r\n        }\r\n        return null;\r\n    }\r\n\r\n##M\r\n\r\n代码 : \r\n\r\n    /**\r\n     * 获取数据库实例\r\n     * @return DB\r\n     */\r\n    function M(){\r\n        return \\Seter\\Seter::getInstance()->db;\r\n    }\r\n\r\n##R\r\n\r\n代码 : \r\n\r\n\r\n    //页面跳转\r\n    function R($url, $time=0, $msg='''') {\r\n        $url = str_replace(array("\\n", "\\r"), '''', $url);\r\n        if (empty($msg))\r\n            $msg = "系统将在{$time}秒之后自动跳转到{$url}！";\r\n        if (!headers_sent()) {\r\n            // redirect\r\n            if (0 === $time) {\r\n                header(''Location: '' . $url);\r\n            } else {\r\n                header("refresh:{$time};url={$url}");\r\n                echo($msg);\r\n            }\r\n            exit();\r\n        } else {\r\n            $str = "<meta http-equiv=''Refresh'' content=''{$time};URL={$url}''>";\r\n            if ($time != 0)\r\n                $str .= $msg;\r\n            exit($str);\r\n        }\r\n    }\r\n\r\n\r\n##T\r\n\r\n代码 : \r\n\r\n    /**\r\n     * 返回当前时间戳 毫秒\r\n     * @return float\r\n     */\r\n    function T(){\r\n        list($usec, $sec) = explode(" ",microtime());\r\n        $num = ((float)$usec + (float)$sec);\r\n        return $num;\r\n    }\r\n\r\n\r\n\r\n##W\r\n\r\n代码 : \r\n\r\n    /**\r\n     * 调用Widget\r\n     * @param string $name widget名\r\n     * @param array $data 传递给widget的变量列表，key为变量名，value为变量值\r\n     * @return void\r\n     */\r\n    function W($name, $data = array()){\r\n        $fullName = $name.''Widget'';\r\n        if(!class_exists($fullName)){\r\n            halt(''Widget ''.$name.''不存在'');\r\n        }\r\n        $widget = new $fullName();\r\n        $widget->invoke($data);\r\n    }\r\n\r\n\r\n##truepath\r\n\r\n代码 : \r\n\r\n      function truepath($path) {\r\n        //是linux系统么？\r\n        $unipath = PATH_SEPARATOR == '':'';\r\n        //检测一下是否是相对路径，windows下面没有:,linux下面没有/开头\r\n        //如果是相对路径就加上当前工作目录前缀\r\n        if (strpos($path, '':'') === false && strlen($path) && $path{0} != ''/'') {\r\n            $path = realpath(''.'') . DIRECTORY_SEPARATOR . $path;\r\n        }\r\n        $path = str_replace(array(''/'', ''\\\\''), DIRECTORY_SEPARATOR, $path);\r\n        $parts = array_filter(explode(DIRECTORY_SEPARATOR, $path), ''strlen'');\r\n        $absolutes = array();\r\n        foreach ($parts as $part) {\r\n            if (''.'' == $part)\r\n                continue;\r\n            if (''..'' == $part) {\r\n                array_pop($absolutes);\r\n            } else {\r\n                $absolutes[] = $part;\r\n            }\r\n        }\r\n        //如果是linux这里会导致linux开头的/丢失\r\n        $path = implode(DIRECTORY_SEPARATOR, $absolutes);\r\n        //如果是linux，修复系统前缀\r\n        $path = $unipath ? (strlen($path) && $path{0} != ''/'' ? ''/'' . $path : $path) : $path;\r\n        //最后统一分隔符为/，windows兼容/\r\n        $path = str_replace(array(''/'', ''\\\\''), ''/'', $path);\r\n        return $path;\r\n    }\r\n\r\n\r\n\r\n##includeIfExist\r\n\r\n代码 : \r\n\r\n    /**\r\n     * 如果文件存在就include进来\r\n     * @param string $path 文件路径\r\n     * @return void\r\n     */\r\n    function includeIfExist($path){\r\n        if(file_exists($path)){\r\n            include $path;\r\n        }\r\n    }\r\n\r\n##GetIP\r\n\r\n代码 : \r\n\r\n    /**\r\n     * 获取IP地址\r\n     * @param type null\r\n     * @return string like ''12.70.0.1''\r\n     */\r\n    function GetIP(){\r\n        if(!empty($_SERVER["HTTP_CLIENT_IP"])){\r\n            $cip = $_SERVER["HTTP_CLIENT_IP"];\r\n        }\r\n        elseif(!empty($_SERVER["HTTP_X_FORWARDED_FOR"])){\r\n            $cip = $_SERVER["HTTP_X_FORWARDED_FOR"];\r\n        }\r\n        elseif(!empty($_SERVER["REMOTE_ADDR"])){\r\n            $cip = $_SERVER["REMOTE_ADDR"];\r\n        }\r\n        else{\r\n            $cip = "无法获取！";\r\n        }\r\n        return $cip;\r\n    }\r\n\r\n##URLES\r\n\r\n代码 : \r\n\r\n    function RULES(){\r\n        $rules = $rules_ = [];\r\n        if(file_exists(C(''app'')[''APP_PATH''].''Rules.php'')){\r\n            $rules = include C(''app'')[''APP_PATH''].''Rules.php'';\r\n        }\r\n        if(!empty(C(''Router'')[''method_modules''])){\r\n            $rulepath =  C(''Router'')[''Appbase''].''Rules.php'';\r\n            if(file_exists($rulepath)){\r\n                $rules_ = include $rulepath;\r\n            }\r\n        }\r\n        if(!empty($rules_)){\r\n            $access = array_merge($rules[''access''][''rules''],$rules_[''access''][''rules'']?:[]);\r\n            unset($rules[''access''][''rules'']);\r\n            unset($rules_[''access''][''rules'']);\r\n            $rules = array_merge($rules,$rules_?:[]);\r\n            $rules[''access''][''rules''] = $access;\r\n        }\r\n        return $rules;\r\n    }\r\n\r\n\r\n##halt\r\n\r\n代码 : \r\n\r\n    /**\r\n     * 终止程序运行\r\n     * @param string $str 终止原因\r\n     * @param bool $display 是否显示调用栈，默认不显示\r\n     * @return void\r\n     */\r\n    function halt($str, $display=false){\r\n        //Log::fatal($str.'' debug_backtrace:''.var_export(debug_backtrace(), true));\r\n        header("Content-Type:text/html; charset=utf-8");\r\n        if($display){\r\n            echo "<pre>";\r\n            debug_print_backtrace();\r\n            echo "</pre>";\r\n        }\r\n        echo $str;\r\n        exit;\r\n    }\r\n\r\n\r\n\r\n##error404\r\n\r\n代码 : \r\n\r\n    /**\r\n     * 404错误页面\r\n     * 页面内容 C(''error_page_404'');设置\r\n     */\r\n    function error404()\r\n    {\r\n        header(''HTTP/1.1 404 Not Found'');\r\n        header("status: 404 Not Found");\r\n        include C(''Router'')[''Appbase''].C(''app'')[''error_page_404''];\r\n        exit;\r\n    }\r\n\r\n##errormsg\r\n\r\n代码 : \r\n\r\n\r\n    /**\r\n     * 提示信息页面\r\n     * @param string $msg\r\n     * 页面设置 C(''error_page_msg'');\r\n     */\r\n    function errormsg($msg = '''')\r\n    {\r\n        include C(''Router'')[''Appbase''].C(''app'')[''error_page_msg''];\r\n        //include C(''error_page_msg'');\r\n        exit;\r\n    }\r\n\r\n##error500\r\n\r\n代码 : \r\n\r\n    /**\r\n     * 500错误页面\r\n     * 设置C(''error_page_500'');\r\n     */\r\n    function error500()\r\n    {\r\n        header(''HTTP/1.1 500 Error Found'');\r\n        header("status: 500 Not Found");\r\n        include C(''error_page_500'');\r\n        exit;\r\n    }\r\n', NULL, 0, 1),
